import React from 'react';

import { Link } from 'react-router-dom';

import './game-page.css';

import Bar from '../../bar'

const questions = require("./questions.json");


export default class GamePage extends React.Component {

	state = {
		pos: 1,
		choose: true,
		answer: '',
	}

	clickItem = e => {
		if(e.target.className != "game_content_answer" || !this.state.choose)
			return;

		this.setState({
			choose: false,
			answer: e.target.innerHTML
		}, () =>setTimeout(this.check, 1000))
	}

	check = () => {


		const {answer, pos } = this.state; 

		const answer_right = questions[pos].right; 

		const new_pos = answer == answer_right ? pos + 1  : this.lose() 

		this.setState({
			choose: true,
			answer: '',
			pos: new_pos
		})
	}

	lose = () => {
		alert('Вы проебали!Можно начать заново!');

		return 0
	}

	render() {
		const { pos, answer, right, noright } = this.state

		const item = questions[pos];

		return (
			<div className="game">
				<div className="game_main">

					<Bar pos={pos}/>

					<div className="game_main_bg"></div>
				</div>

				<div className="game_footer">
					<div className="game_content">
						<div className="game_content_question">{item.question}</div>
						<div className="game_content_answers" onClick={this.clickItem}>
							{ 
								item.answers.map(el => {
									let className = "game_content_answer";

									if(el == answer) className += " answer_choose";
									if(el == item.right) className += " answer_right";

									{/*if(noright) {
										if(el == item.right) className += " answer_right";
										if(el == noright) className += " answer_false";
									}*/}

									return (
										<div key={el} className={className}>
											{el}
										</div>
									)	
								})
							}
						</div>
					</div>
				</div>
			</div>

		)
	}
}
