import React from 'react';

import { Link } from 'react-router-dom';

import './game-page.css';

import Bar from '../../bar'

const questions = require("./questions.json");


export default class GamePage extends React.Component {

	state = {
		pos: 1,
		reject: false,
		answer_user: '',
		answer_right: state.pos,
		right: false,
		// noright: false
	}

	clickItem = e => {
		if(e.target.className != "game_content_answer" || this.state.reject)
			return;

		this.setState({
			reject: true,
			answer_user: e.target.innerHTML
		})
	}

	check = (el, right) => {
		setTimeout(() => {
			el == right ? this.right(el) : this.noright(el)
		}, 1000)
	}

	// noright = el => {

	// 	this.setState({
	// 		answer: false,
	// 		noright: el
	// 	})

	// 	setTimeout(this.lose, 2000)	
	// }

	right = (el) => {
		if(this.state.right)
			return

		this.setState({
			right: el
		})

		setTimeout(this.nextQuestion, 2000)
	}


	nextQuestion = () => {
		this.setState({
			right: false,
			reject: false,
			pos: this.state.pos + 1
		})
	}

	lose = () => {
		alert('Вы проебали!Можно начать заново!')

		this.setState({
			noright: false,
			right: false,
			reject: false,
			answer: '',
			pos: 0
		})
	}

	render() {
		const { pos, answer, right, noright } = this.state

		const item = questions[pos];

		return (
			<div className="game">
				<div className="game_main">

					<Bar pos={pos}/>

					<div className="game_main_bg"></div>
				</div>

				<div className="game_footer">
					<div className="game_content">
						<div className="game_content_question">{item.question}</div>
						<div className="game_content_answers" onClick={this.clickItem}>
							{ 
								item.answers.map(el => {
									let className = "game_content_answer";

									if(el == answer) {
										className += " answer_choose"

										this.check(el, item.right);
									}

									if(el == right) 
										className += " answer_right";

									if(noright) {
										if(el == item.right) 
											className += " answer_right";

										if(el == noright) 
											className += " answer_false";
									}

									return (
										<div key={el} className={className}>
											{el}
										</div>
									)	
								})
							}
						</div>
					</div>
				</div>
			</div>

		)
	}
}
