import React from 'react';

import { Link } from 'react-router-dom';

import './game-page.css';

import Bar from '../../bar'

const questions = require("./questions.json");


export default class GamePage extends React.Component {

	state = {
		pos: 1,
		reject: false,
		answer: '',
		right: false,
		noright: false
	}

	clickItem = e => {
		if(e.target.className != "game_content_answer" || this.state.reject)
			return;

		this.setState({
			reject: true,
			answer: e.target.innerHTML
		})
	}

	noright = (el) => {

		this.setState({
			answer: false,
			noright: el
		})
	}

	right = (el) => {
		if(this.state.right)
			return

		this.setState({
			right: el
		})
	}

	nextQuestion = () => {
		this.setState({
			right: false,
			reject: false,
			pos: this.state.pos + 1
		})
	}

	lose = () => {
		alert('Вы проебали!Можно начать заново!')

		this.setState({
			noright: false,
			right: false,
			reject: false,
			answer: '',
			pos: 0
		})
	}

	render() {
		const { pos, answer, right, noright } = this.state

		const item = questions[pos];

		return (
			<div className="game">
				<div className="game_main">

					<Bar pos={pos}/>

					<div className="game_main_bg"></div>
				</div>

				<div className="game_footer">
					<div className="game_content">
						<div className="game_content_question">{item.question}</div>
						<div className="game_content_answers" onClick={this.clickItem}>
							{ 
								item.answers.map(el => {
								let className = "game_content_answer";

								if(el == answer) {
									className += " answer_choose"

									if(answer == item.right) {
										setTimeout(() => {
											this.right(el)
										}, 1000)
									} else {
										setTimeout(() => {
											this.noright(el)
										}, 1000)
									}
								}

								if(right == el) {
									className += " answer_right";

									setTimeout(this.nextQuestion
									, 2000)
								}

								if(noright == el) {
									className += " answer_false";								

								}

								if(noright) {
									if(el == item.right) {
										className += " answer_right";

										setTimeout(this.lose
										, 2000)	
									}

								}

								return (
									<div key={el} className={className}>
										{el}
									</div>
								)	
							})}
						</div>
					</div>
				</div>
			</div>

		)
	}
}
